import { useState } from 'react';
import { evaluate } from 'mathjs';
import Boton from './components/boton.js';
import Backspace from './images/svg/backspace.svg';

let buttonsContent = ['√','³√','xⁿ','÷','7','8','9','*','4','5','6','-','1','2','3','+','±','0','.','=','()','C'];

function App() {

  const [dm, setDm] = useState([]);

  const altura = (altur)=>{
    setDm([altur.target.getBoundingClientRect().width,altur.target.getBoundingClientRect().height]);
  }

  return (
    <div className='calculator'>
      <input type='text' className='text' id='text' readOnly='readonly'/>
      {
        buttonsContent.map(boton=>{
          return <Boton clase='buttons'>{boton}</Boton>
        })
      }
      <Boton clase='buttons' id='BACK' funcionPrueba={altura}><img src={Backspace}/></Boton>
    </div>
  );
}

export default App;
