import React from 'react';

function Boton({props, funcion}){
  const esOperador = valor =>{
    return isNaN(valor) && (valor != '.') && (valor != '=') && (valor != 'Clear');
  }

  return (
    <button className={`boton ${esOperador(props.children) ? 'operador' : props.children == "Clear" ? 'clear' : ""}`.trimEnd()} onClick={funcion(props.children)}>
      {props.children}
    </button>
  );
};

export default Boton;
