import './App.css';
import freeCodeCampLogo from './imagenes/freecodecamplogo.png';
import Boton from './componentes/boton';
import { useState } from 'react';
import { evaluate } from 'mathjs';

function App() {

  const [Input, setInput] = useState('');

  const write = val =>{
    setInput(val == 'Clear' ? '' : (val == '=' && input) ? evaluate(Input) : Input + val);
  }

  return (
    <div className="App">
      <div className='freecodecamp-logo-contenedor'>
        <img 
          src={freeCodeCampLogo}
          className="freecodecamp-logo"
          alt='Logo de freeCodeCamp'
        />
      </div>
      <div className='contenedor-calculadora'>
        <input type='text' className='calculadora-texto' value={Input}/>
        <div className='filas'>
          <Boton funcion={write}>7</Boton>
          <Boton funcion={write}>8</Boton>
          <Boton funcion={write}>9</Boton>
          <Boton funcion={write}>+</Boton>
          <Boton funcion={write}>4</Boton>
          <Boton funcion={write}>5</Boton>
          <Boton funcion={write}>6</Boton>
          <Boton funcion={write}>-</Boton>
          <Boton funcion={write}>1</Boton>
          <Boton funcion={write}>2</Boton>
          <Boton funcion={write}>3</Boton>
          <Boton funcion={write}>*</Boton>
          <Boton funcion={write}>=</Boton>
          <Boton funcion={write}>0</Boton>
          <Boton funcion={write}>.</Boton>
          <Boton funcion={write}>/</Boton>
          <Boton funcion={write}>Clear</Boton>
        </div>
      </div>
    </div>
  );
}

export default App;
