import { useState } from 'react';
import { evaluate } from 'mathjs';
import sunny from './images/svg/sunny.svg'; import moon from './images/svg/moon.svg';
import close from './images/svg/close.svg'; import help from './images/svg/help-outline.svg';
import Boton from './components/boton.js';
import Backspace from './images/svg/backspace.svg';

let buttonsContent = ['√','³√','xⁿ','÷','7','8','9','*','4','5','6','-','1','2','3','+','±','0','.','=','()','C'];

function App({pantalla}) {

  const darkMode = ()=>{
    document.querySelector('.body').classList.toggle('dark-mode');
  }

  const [dm, setDm] = useState([]);

  const altura = (altur)=>{
    const alturas = [altur.target.getBoundingClientRect().width,altur.target.getBoundingClientRect().height];
    setDm(dm=> dm = alturas);
    console.log(alturas);
    console.log(dm);
  }

  return (
    <>
    <span className="darkMode" /*style={pantalla.width <= 460 ? `width:${dm[0]}px !important;height:${dm[1]}px !important;` : ''}*/ onClick={darkMode}>
		  <img className='sunny' src={sunny} />
		  <img className='moon' src={moon} />
	  </span>
	  <span className="about-button">
	  	<img className='help' src={help} />
	  	<img className='close' src={close} />
	  </span>
    <div className='calculator'>
      <input type='text' className='text' id='text' readOnly='readonly'/>
      {
        buttonsContent.map(boton=>{
          return <Boton clase='buttons'>{boton}</Boton>
        })
      }
      <Boton clase='buttons' id='BACK' funcionPrueba={altura}><img src={Backspace}/></Boton>
    </div>
    </>
  );
}

export default App;
