import {useState} from 'react'; import ReactDOM from 'react-dom/client';
import './css/normalize.css'; import './css/style.css';
import jfast from './images/icons/jfastSFX.svg'; import githubLogo from './images/icons/logo-github.svg';
import sunny from './images/svg/sunny.svg'; import moon from './images/svg/moon.svg';
import close from './images/svg/close.svg'; import help from './images/svg/help-outline.svg';
import App from './main';

const darkMode = ()=>{
  document.querySelector('.body').classList.toggle('dark-mode');
}

const [dm, setDm] = useState([]);

const altura = (altur)=>{
  setDm([altur.target.getBoundingClientRect().width,altur.target.getBoundingClientRect().height]);
}

const root = ReactDOM.createRoot(document.querySelector('.body'));
root.render(
  <React.StrictMode>
    <section className='about'>
     <p>Todos los derechos reservados para "Ionic" y el uso de sus iconos "Ion-icons" &#169; <b className="year">{new Date().getFullYear()}</b></p>
		  <a href="https://github.com/Josafast" target="_BLANK"><img src={jfast} /></a>
		  <a href="https://github.com/Josafast/Calculator"><img src={githubLogo} /></a>
    </section>
    <span className="darkMode" /*style={document.body.clientWidth <= 460 ? `width:${document.querySelectorAll(".buttons")[0].clientWidth}px !important;height:${document.querySelectorAll(".buttons")[0].clientHeight}px !important` : ''}*/ onClick={darkMode}>
		  <img className='sunny' src={sunny} />
		  <img className='moon' src={moon} />
	  </span>
	  <span className="about-button">
	  	<img className='help' src={help} />
	  	<img className='close' src={close} />
	  </span>
    <App funcionPrueba={altura}/>
  </React.StrictMode>
);

if (document.querySelector('.body').clientWidth <= 460) {
	document.querySelector(".darkMode").style = `width:${document.querySelectorAll(".buttons")[0].getBoundingClientRect().width}px !important;height:${document.querySelectorAll(".buttons")[0].getBoundingClientRect().height}px !important`;
	document.querySelector(".about-button").setAttribute("style",`width:${document.querySelectorAll(".buttons")[0].clientWidth}px !important;height:${document.querySelectorAll(".buttons")[0].clientHeight}px !important;`);
}
