import React from 'react';

function Boton(props){
  const esOperador = valor =>{
    return isNaN(valor);
  }

  return (
    <button className={`boton${esOperador(props.children) ? 'operador' : ""}`}>
      {props.children}
    </button>
  );
};

export default Boton;
